                  //ok
                  if(i != null){ 
                  }
                  //ok
                  if(h != false){ 
                  }    
                  for(int j=0 ; j< 10 ; i++){
               	     //ok
               	     if(i > 2){ 
                     }
                     //ok
                     if(i != null){ 
                     }   
                  }     
                  while(k < 1){
                     //ok
                     if(i > 2){ 
                     }
                     k++;
                  }
