public class AvoidCommentBehindStatement {
    private String name;
    public String getName() {
        // 上一行注释1
        return this.name; 
    }
    public void setName(String name) {
        // 上一行注释2
        this.name = name; 
    }
}

public class AvoidCommentBehindStatement {
    private String name;
    public String getName() {
        return this.name; // 同行注释1
    }
    public void setName(String name) {
        this.name = name; // 同行注释2
    }
}
