public class Example {
    public void fn() {
    	int a = 2;
    	int b = 4;

    	if (a > 0) b++;			// BAD

    	if (a > 0) {
    		b++;
    	} else if (a > -1) {
    		b++;
    	} else
    		b--;				// BAD

    	for (a = 0; a < 3; a ++) b--;	// BAD

		while (a > 3) a--;				// BAD
    }
}


