    public void fn() {
    	int i;
    	switch (i) {
    	    case 0:
    	    	break;
    	    case 1:
    	    	break;
    	    // 缺少default语句
    	}
    }
}

public class Example {
    public void fn() {
    	int i;
    	switch (i) {
    	    case 0:
    	    	// 缺少break,return,continue
    	    case 1:
    	    	break;
    	    case 2:
    	    	continue;
    	    default:
    	    	return;
    	}
    }
}
