public class Example {
    public void fn() {
    	int i;
    	switch (i) {
    	    case 0:
    	    	break;
    	    case 1:
    	    	int j;
    	    	switch (j) {
    	    		case 0:
    	    			break;
    	    		default:	// 嵌套的switch包含了default分支
    	    			return;
    	    	}
    	    	break;
    	    // 缺少default语句
    	}
    }
}


public class Foo {
    public void bar() {
    	int i;
    	switch (i) {
    	    case 0:
    	    	break;
    	    case 1:
    	    	int j;
    	    	switch (j) {
    	    		case 0:
    	    			break;
    	    		// 嵌套的switch缺少default分支
    	    	}
    	    	break;
    	    default:
    	    	return;
    	}
    }
}


public class Foo {
    public void bar() {
    	int i;
    	switch (i) {
    	    case 0:
    	    	int j =1;
    	    	// 缺少break,return,continue
    	    case 1:
    	    	break;
    	    case 2:
    	    	continue;
    	    case 3:		// OK,空语句忽略break检查
    	    default:
    	    	return;
    	}
    }
}
