public class FooDO {
	private static final String HELLO = "Hello";	// OK
	private String tom = ""; 	// BAD
	private String jerry;		// OK
	private int pageSize = 20;	// BAD

	public void bar() {
		int pageNo = 1;
	}

	private static class NestedDTO {
		private String tom = ""; // BAD
	}

	private static class InnerClass {
		private String tom = ""; // OK
	}
}


public class FooPO {
	private static final String HELLO = "Hello";	// OK
	private String tom = ""; 	// OK,非POJO不检查
	private String jerry;		// OK
	private int pageSize = 20;	// OK,非POJO不检查

	public void bar() {
		int pageNo = 1;
	}

	private static class NestedDTO {
		private String tom = ""; // BAD
	}
	private static class InnerClass {
		private String tom = ""; // OK
	}
}


public class FooDAO {
	private static final String HELLO = "Hello";	// OK
	private String tom = ""; 	// BAD
	private String jerry;		// OK
	public int pageSize = 20;	// OK,public

	public void bar() {
		int pageNo = 1;
	}

	private static class NestedDTO {
		private String tom = ""; // BAD
	}
}
